class App
{

  public function __construct()
  {

    if(!Dir::exists(PUBLICFOLDER.'/audio')) Dir::create(PUBLICFOLDER.'/audio');
    if(!Dir::exists(PUBLICFOLDER.'/documents')) Dir::create(PUBLICFOLDER.'/documents');
    if(!Dir::exists(PUBLICFOLDER.'/images')) Dir::create(PUBLICFOLDER.'/images');
    if(!Dir::exists(PUBLICFOLDER.'/video')) Dir::create(PUBLICFOLDER.'/video');
    
  }

  /**
   * Is Logged
   * 
   * @return boolean
   */
  public static function isLogged()
  {
    if(Session::get('api_token') && 
      Session::get('api_email') && 
      Session::get('api_name')){
      return true;
    }
    return false;
  }

  /**
   * Is Admin
   * 
   * @return boolean
   */
  public static function isAdmin()
  {
    if(Session::get('api_role') == "admin") return true;
    return false;
  }

  /**
   * Url Convert
   * 
   * @param string $file
   * 
   * @return string
   */
  public static function urlConvert($file)
  {
     $file = str_replace(ROOT,Url::base(),$file);
     return $file;
  }

  /**
   * Generate random password
   * 
   * url: https://stackoverflow.com/questions/6101956/generating-a-random-password-in-php
   * @param string $file
   * 
   * @return string
   */
  public static function randomPassword($num=8) {
      $alphabet = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890';
      $pass = array(); //remember to declare $pass as an array
      $alphaLength = strlen($alphabet) - 1; //put the length -1 in cache
      for ($i = 0; $i < $num; $i++) {
          $n = rand(0, $alphaLength);
          $pass[] = $alphabet[$n];
      }
      return implode($pass); //turn the array into a string
  }


  /**
   * Init Database
   *
   * @param string $name
   * @return array 
   */
  public static function Db($name)
  {
    return new db("sqlite:$name");
  }

  /** 
   *  load extensions nav
   * 
   * @return string
   */
  public static function loadExtNav()
  {
    // http://stackoverflow.com/questions/14680121/require-just-files-in-scandir-array
    $extensions = array_filter(
      scandir(EXTENSIONS),
      function ($item) {
          return $item[0] !== '.';
      }
    );

    foreach ($extensions as $ext) {
      $jsonFile = EXTENSIONS.'/'.$ext.'/config.json';
      // if exists json file load plugin
      if (file_exists($jsonFile)) {
          // get contents of file
          $extensionsFile = file_get_contents($jsonFile);
      }
      // convert to json
      $json = json_decode($extensionsFile, true);
      // loop to read json
      if (!is_array($json)) continue;

      foreach ($json as $obj) {
          $name = $obj['filename'];
          $enabled = $obj['enabled'];
          $routesFile = EXTENSIONS.'/'.$name.'/src/routes.php';
          if ($enabled) {
              if (file_exists($routesFile)) {
                 if(preg_match("/login/s",$routesFile)) continue;
                 $cls = (Url::segment(0) == $name) ? 'text-primary': '';
                 echo '<a  class="dash-nav-item '.$cls.' " href="'.Url::base().'/'.$obj['filename'].'"> 
                      <i class="'.$obj['icon'].'"></i>'.$obj['name'].' 
                    </a>';
              }
          }
      }
    }
  }

  /** 
   *  Active extension
   * 
   * @param string $name
   *
   * @return string
   */
  public static function isActiveExt($name)
  {
    // http://stackoverflow.com/questions/14680121/require-just-files-in-scandir-array
    $extensions = array_filter(scandir(EXTENSIONS),function ($item) {
          return $item[0] !== '.';
    });
    foreach ($extensions as $ext) {
      $jsonFile = EXTENSIONS.'/'.$ext.'/config.json';
      // if exists json file load plugin
      if (file_exists($jsonFile)) $extensionsFile = file_get_contents($jsonFile);
      // convert to json
      $json = json_decode($extensionsFile, true);
      // loop to read json
      if (!is_array($json)) continue;
      foreach ($json as $obj) {
        if($obj['filename'] == $name){
          if($obj['enabled']){
            return true;
          }
        }
      }
    }
  }

  /**
   * Get page
   *
   * @param array $data
   *
   * @param array $data
   */
  public static function page($data = array())
  {
    $T = new Template();
    $T->set('data',$data);
    return $T->draw(ROOT.'/core/views/layout.html');
  }

  /**
  * Get log in array format
  * 
  * @return array
  */
  public static function log(){
    $file = json_decode(file_get_contents(ROOT.'/log.txt'),true);
    return array_reverse($file);
  }

  /**
   * Clean log
   */
  public static function cleanLog()
  {
    if(array_key_exists('clearlog',$_POST)){
      File::delete(ROOT.'/log.txt');
      Debug::log('The log is clear');
      Message::set('The log is clear');
      Url::redirect(Url::base());
    }
  }

  /**
   * Get partial
   * 
   * @param string $name
   */
  public static function partial($file = "")
  {
    if(File::exists($file)){
      return File::read($file);
    }else{
      $msg = 'Error section '.File::name($file).' not found';
      Debug::log($msg);
      return $msg;
    }
  }

  /**
   * 404 Access Deneid
   *
   * @return string image
   */
  public static function die()
  {
    $html = '<style>body{background:black;color:aqua;font-family:"Consolas",monospace;margin:3em auto;text-align:center;}</style>';
    $html .= '<img src="data:image/png;base64,"/>';
    $html .= "<p>You don't have access here.</p>";
    $html .= "<p>Redirect on <span id=\"count\">5</span></p>";
    $html .= "<script>var num = 4; setInterval(function(){window.count.textContent = num--;if(num === 0) location.href='https://google.com';},1000)</script>";
    die($html);
  }

  /**
   * Show Error span
   * 
   * @param string $msg - the message
   * @return string 
   */
  public static function error($msg="")
  {
    $content = "<span style=\"display: inline-block; background: #f55; color: white; padding: 4px 10px; border-radius: 4px; font-family: 'Lucida Console', Monaco, monospace, sans-serif; font-size: 80%\"><b style=\"color:#FFEB3B;\">Error</b>: {$msg}</span>";
    return $content;
  }
}